def escape(cls, s: t.Any, /) -> te.Self:
    """Escape a string. Calls :func:`escape` and ensures that for
    subclasses the correct type is returned.
    """
    rv = escape(s)

    if rv.__class__ is not cls:
        return cls(rv)

    return rv  # type: ignore[return-value]

