sympy.sympy.assumptions.refine.refine_Po:
def refine_Pow(expr, assumptions):
    """
    Handler for instances of Pow.

    Examples
    ========

    >>> from sympy import Q
    >>> from sympy.assumptions.refine import refine_Pow
    >>> from sympy.abc import x,y,z
    >>> refine_Pow((-1)**x, Q.real(x))
    >>> refine_Pow((-1)**x, Q.even(x))
    1
    >>> refine_Pow((-1)**x, Q.odd(x))
    -1

    For powers of -1, even parts of the exponent can be simplified:

    >>> refine_Pow((-1)**(x+y), Q.even(x))
    (-1)**y
    >>> refine_Pow((-1)**(x+y+z), Q.odd(x) & Q.odd(z))
    (-1)**y
    >>> refine_Pow((-1)**(x+y+2), Q.odd(x))
    (-1)**(y + 1)
    >>> refine_Pow((-1)**(x+3), True)
    (-1)**(x + 1)

    """
    from sympy.functions.elementary.complexes import Abs
    from sympy.functions import sign
    if isinstance(expr.base, Abs):
        if ask(Q.real(expr.base.args[0]), assumptions) and \
                ask(Q.even(expr.exp), assumptions):
            return expr.base.args[0] ** expr.exp
    if ask(Q.real(expr.base), assumptions):
        if expr.base.is_number:
            if ask(Q.even(expr.exp), assumptions):
                return abs(expr.base) ** expr.exp
            if ask(Q.odd(expr.exp), assumptions):
                return sign(expr.base) * abs(expr.base) ** expr.exp
        if isinstance(expr.exp, Rational):
            if isinstance(expr.base, Pow):
                return abs(expr.base.base) ** (expr.base.exp * expr.exp)

        if expr.base is S.NegativeOne:
            if expr.exp.is_Add:

                old = expr

                # For powers of (-1) we can remove
                #  - even terms
                #  - pairs of odd terms
                #  - a single odd term + 1
                #  - A numerical constant N can be replaced with mod(N,2)

                coeff, terms = expr.exp.as_coeff_add()
                terms = set(terms)
                even_terms = set()
                odd_terms = set()
                initial_number_of_terms = len(terms)

                for t in terms:
                    if ask(Q.even(t), assumptions):
                        even_terms.add(t)
                    elif ask(Q.odd(t), assumptions):
                        odd_terms.add(t)

                terms -= even_terms
                if len(odd_terms) % 2:
                    terms -= odd_terms
                    new_coeff = (coeff + S.One) % 2
                else:
                    terms -= odd_terms
                    new_coeff = coeff % 2

                if new_coeff != coeff or len(terms) < initial_number_of_terms:
                    terms.add(new_coeff)
                    expr = expr.base**(Add(*terms))

                # Handle (-1)**((-1)**n/2 + m/2)
                e2 = 2*expr.exp
                if ask(Q.even(e2), assumptions):
                    if e2.could_extract_minus_sign():
                        e2 *= expr.base
                if e2.is_Add:
                    i, p = e2.as_two_terms()
                    if p.is_Pow and p.base is S.NegativeOne:
                        if ask(Q.integer(p.exp), assumptions):
                            i = (i + 1)/2
                            if ask(Q.even(i), assumptions):
                                return expr.base**p.exp
                            elif ask(Q.odd(i), assumptions):
                                return expr.base**(p.exp + 1)
                            else:
                                return expr.base**(p.exp + i)

                if old != expr:
                    return expr

sympy.sympy.core.cache.wrapper:
@wraps(func)
def wrapper(*args, **kwargs):
    try:
        retval = cfunc(*args, **kwargs)
    except TypeError as e:
        if not e.args or not e.args[0].startswith('unhashable type:'):
            raise
        retval = func(*args, **kwargs)
    return retval

sympy.sympy.core.operations.__new__:
def __new__(cls, *args, evaluate=None, **options):
    args = (_sympify_(arg) for arg in args)

    if evaluate is None:
        evaluate = global_parameters.evaluate

    if not evaluate:
        obj = super().__new__(cls, *args, evaluate=False, **options)
        obj._argset = frozenset(args)
        return obj

    try:
        # /!\ args is a generator and _new_args_filter
        # must be careful to handle as such; this
        # is done so short-circuiting can be done
        # without having to sympify all values
        _args = frozenset(cls._new_args_filter(args))
    except ShortCircuit:
        return sympify(cls.zero)
    if not _args:
        return sympify(cls.identity)
    elif len(_args) == 1:
        return set(_args).pop()
    else:
        # XXX in almost every other case for __new__, *_args is
        # passed along, but the expectation here is for _args
        obj = super(AssocOp, cls).__new__(cls, *ordered(_args))
        obj._argset = _args
        return obj

sympy.sympy.core.power._eval_refine:
def _eval_refine(self, assumptions):
    from sympy.assumptions.ask import ask, Q
    b, e = self.as_base_exp()
    if ask(Q.integer(e), assumptions) and b.could_extract_minus_sign():
        if ask(Q.even(e), assumptions):
            return Pow(-b, e)
        elif ask(Q.odd(e), assumptions):
            return -Pow(-b, e)

sympy.sympy.core.relational.__new__:
def __new__(cls, lhs: Expr | complex, rhs: Expr | complex, **options) -> Self | BooleanTrue | BooleanFalse: # type: ignore

    try:
        lhs_e = _sympify(lhs)
        rhs_e = _sympify(rhs)
    except SympifyError:
        return NotImplemented

    evaluate = options.pop('evaluate', global_parameters.evaluate)
    if evaluate:
        for me in (lhs_e, rhs_e):
            if me.is_extended_real is False:
                raise TypeError("Invalid comparison of non-real %s" % me)
            if me is S.NaN:
                raise TypeError("Invalid NaN comparison")
        # First we invoke the appropriate inequality method of `lhs`
        # (e.g., `lhs.__lt__`).  That method will try to reduce to
        # boolean or raise an exception.  It may keep calling
        # superclasses until it reaches `Expr` (e.g., `Expr.__lt__`).
        # In some cases, `Expr` will just invoke us again (if neither it
        # nor a subclass was able to reduce to boolean or raise an
        # exception).  In that case, it must call us with
        # `evaluate=False` to prevent infinite recursion.
        return cls._eval_relation(lhs_e, rhs_e, **options)

    # make a "non-evaluated" Expr for the inequality
    return Relational.__new__(cls, lhs_e, rhs_e, **options)

sympy.sympy.functions.elementary.piecewi:
def __new__(cls, expr, cond):
    expr = as_Basic(expr)
    if cond == True:
        return Tuple.__new__(cls, expr, true)
    elif cond == False:
        return Tuple.__new__(cls, expr, false)
    elif isinstance(cond, Basic) and cond.has(Piecewise):
        cond = piecewise_fold(cond)
        if isinstance(cond, Piecewise):
            cond = cond.rewrite(ITE)

    if not isinstance(cond, Boolean):
        raise TypeError(filldedent('''
            Second argument must be a Boolean,
            not `%s`''' % func_name(cond)))
    return Tuple.__new__(cls, expr, cond)

sympy.sympy.logic.boolalg._eval_refine:
def _eval_refine(self, assumptions):
    from sympy.assumptions import ask
    ret = ask(self, assumptions)
    if ret is True:
        return true
    elif ret is False:
        return false
    return None

