@wraps(func)
def wrapper(*args, **kwargs):
    try:
        retval = cfunc(*args, **kwargs)
    except TypeError as e:
        if not e.args or not e.args[0].startswith('unhashable type:'):
            raise
        retval = func(*args, **kwargs)
    return retval

sympy.sympy.core.expr.coeff:
def coeff(self, x: Expr | complex, n=1, right=False, _first=True):
    """
    Returns the coefficient from the term(s) containing ``x**n``. If ``n``
    is zero then all terms independent of ``x`` will be returned.

    Explanation
    ===========

    When ``x`` is noncommutative, the coefficient to the left (default) or
    right of ``x`` can be returned. The keyword 'right' is ignored when
    ``x`` is commutative.

    Examples
    ========

    >>> from sympy import symbols
    >>> from sympy.abc import x, y, z

    You can select terms that have an explicit negative in front of them:

    >>> (-x + 2*y).coeff(-1)
    x
    >>> (x - 2*y).coeff(-1)
    2*y

    You can select terms with no Rational coefficient:

    >>> (x + 2*y).coeff(1)
    x
    >>> (3 + 2*x + 4*x**2).coeff(1)
    0

    You can select terms independent of x by making n=0; in this case
    expr.as_independent(x)[0] is returned (and 0 will be returned instead
    of None):

    >>> (3 + 2*x + 4*x**2).coeff(x, 0)
    3
    >>> eq = ((x + 1)**3).expand() + 1
    >>> eq
    x**3 + 3*x**2 + 3*x + 2
    >>> [eq.coeff(x, i) for i in reversed(range(4))]
    [1, 3, 3, 2]
    >>> eq -= 2
    >>> [eq.coeff(x, i) for i in reversed(range(4))]
    [1, 3, 3, 0]

    You can select terms that have a numerical term in front of them:

    >>> (-x - 2*y).coeff(2)
    -y
    >>> from sympy import sqrt
    >>> (x + sqrt(2)*x).coeff(sqrt(2))
    x

    The matching is exact:

    >>> (3 + 2*x + 4*x**2).coeff(x)
    2
    >>> (3 + 2*x + 4*x**2).coeff(x**2)
    4
    >>> (3 + 2*x + 4*x**2).coeff(x**3)
    0
    >>> (z*(x + y)**2).coeff((x + y)**2)
    z
    >>> (z*(x + y)**2).coeff(x + y)
    0

    In addition, no factoring is done, so 1 + z*(1 + y) is not obtained
    from the following:

    >>> (x + z*(x + x*y)).coeff(x)
    1

    If such factoring is desired, factor_terms can be used first:

    >>> from sympy import factor_terms
    >>> factor_terms(x + z*(x + x*y)).coeff(x)
    z*(y + 1) + 1

    >>> n, m, o = symbols('n m o', commutative=False)
    >>> n.coeff(n)
    1
    >>> (3*n).coeff(n)
    3
    >>> (n*m + m*n*m).coeff(n) # = (1 + m)*n*m
    1 + m
    >>> (n*m + m*n*m).coeff(n, right=True) # = (1 + m)*n*m
    m

    If there is more than one possible coefficient 0 is returned:

    >>> (n*m + m*n).coeff(n)
    0

    If there is only one possible coefficient, it is returned:

    >>> (n*m + x*m*n).coeff(m*n)
    x
    >>> (n*m + x*m*n).coeff(m*n, right=1)
    1

    See Also
    ========

    as_coefficient: separate the expression into a coefficient and factor
    as_coeff_Add: separate the additive constant from an expression
    as_coeff_Mul: separate the multiplicative constant from an expression
    as_independent: separate x-dependent terms/factors from others
    sympy.polys.polytools.Poly.coeff_monomial: efficiently find the single coefficient of a monomial in Poly
    sympy.polys.polytools.Poly.nth: like coeff_monomial but powers of monomial terms are used
    """
    xe = sympify(x)
    if not isinstance(xe, Basic):
        return S.Zero

    n = as_int(n)

    if not xe:
        return S.Zero

    if xe == self:
        if n == 1:
            return S.One
        return S.Zero

    co2: list[Expr]

    if xe is S.One:
        co2 = [a for a in Add.make_args(self) if a.as_coeff_Mul()[0] is S.One]
        if not co2:
            return S.Zero
        return Add(*co2)

    if n == 0:
        if xe.is_Add and self.is_Add:
            c = self.coeff(xe, right=right)
            if not c:
                return S.Zero
            if not right:
                return self - Add(*[a*xe for a in Add.make_args(c)])
            return self - Add(*[xe*a for a in Add.make_args(c)])
        return self.as_independent(xe, as_Add=True)[0]

    # continue with the full method, looking for this power of xe:
    xe = xe**n

    def incommon(l1, l2):
        if not l1 or not l2:
            return []
        n = min(len(l1), len(l2))
        for i in range(n):
            if l1[i] != l2[i]:
                return l1[:i]
        return l1[:]

    def find(l, sub, first=True):
        """ Find where list sub appears in list l. When ``first`` is True
        the first occurrence from the left is returned, else the last
        occurrence is returned. Return None if sub is not in l.

        Examples
        ========

        >> l = range(5)*2
        >> find(l, [2, 3])
        2
        >> find(l, [2, 3], first=0)
        7
        >> find(l, [2, 4])
        None

        """
        if not sub or not l or len(sub) > len(l):
            return None
        n = len(sub)
        if not first:
            l.reverse()
            sub.reverse()
        for i in range(len(l) - n + 1):
            if all(l[i + j] == sub[j] for j in range(n)):
                break
        else:
            i = None
        if not first:
            l.reverse()
            sub.reverse()
        if i is not None and not first:
            i = len(l) - (i + n)
        return i

    co2 = []
    co: list[tuple[set[Expr], list[Expr]]] = []
    args = Add.make_args(self)
    self_c = self.is_commutative
    x_c = xe.is_commutative
    if self_c and not x_c:
        return S.Zero
    if _first and self.is_Add and not self_c and not x_c:
        # get the part that depends on xe exactly
        xargs = Mul.make_args(xe)
        d = Add(*[i for i in Add.make_args(self.as_independent(xe)[1])
            if all(xi in Mul.make_args(i) for xi in xargs)])
        rv = d.coeff(xe, right=right, _first=False)
        if not rv.is_Add or not right:
            return rv
        c_part, nc_part = zip(*[i.args_cnc() for i in rv.args])
        if has_variety(c_part):
            return rv
        return Add(*[Mul._from_args(i) for i in nc_part])

    one_c = self_c or x_c
    xargs, nx = xe.args_cnc(cset=True, warn=bool(not x_c))
    # find the parts that pass the commutative terms
    for a in args:
        margs, nc = a.args_cnc(cset=True, warn=bool(not self_c))
        if nc is None:
            nc = []
        if len(xargs) > len(margs):
            continue
        resid = margs.difference(xargs)
        if len(resid) + len(xargs) == len(margs):
            if one_c:
                co2.append(Mul(*(list(resid) + nc)))
            else:
                co.append((resid, nc))
    if one_c:
        if co2 == []:
            return S.Zero
        elif co2:
            return Add(*co2)
    else:  # both nc
        # now check the non-comm parts
        if not co:
            return S.Zero
        if all(n == co[0][1] for r, n in co):
            ii = find(co[0][1], nx, right)
            if ii is not None:
                if not right:
                    return Mul(Add(*[Mul(*r) for r, c in co]), Mul(*co[0][1][:ii]))
                else:
                    return Mul(*co[0][1][ii + len(nx):])
        beg = reduce(incommon, (n[1] for n in co))
        if beg:
            ii = find(beg, nx, right)
            if ii is not None:
                if not right:
                    gcdc = co[0][0]
                    for i in range(1, len(co)):
                        gcdc = gcdc.intersection(co[i][0])
                        if not gcdc:
                            break
                    return Mul(*(list(gcdc) + beg[:ii]))
                else:
                    m = ii + len(nx)
                    return Add(*[Mul(*(list(r) + n[m:])) for r, n in co])
        end = list(reversed(
            reduce(incommon, (list(reversed(n[1])) for n in co))))
        if end:
            ii = find(end, nx, right)
            if ii is not None:
                if not right:
                    return Add(*[Mul(*(list(r) + n[:-len(end) + ii])) for r, n in co])
                else:
                    return Mul(*end[ii + len(nx):])
        # look for single match
        hit = None
        for i, (r, n) in enumerate(co):
            ii = find(n, nx, right)
            if ii is not None:
                if not hit:
                    hit = ii, r, n
                else:
                    break
        else:
            if hit:
                ii, r, n = hit
                if not right:
                    return Mul(*(list(r) + n[:ii]))
                else:
                    return Mul(*n[ii + len(nx):])

        return S.Zero

sympy.sympy.core.expr.incommon:
def incommon(l1, l2):
    if not l1 or not l2:
        return []
    n = min(len(l1), len(l2))
    for i in range(n):
        if l1[i] != l2[i]:
            return l1[:i]
    return l1[:]

sympy.sympy.core.expr.__pow__:
def __pow__(self, other, mod=None) -> Expr:
    power = self._pow(other)
    if mod is None:
        return power
    else:
        try:
            return power % mod
        except TypeError:
            return NotImplemented

sympy.sympy.core.numbers.__pow__:
def __pow__(self, other, mod=None):
    if mod is not None:
        try:
            other_int = as_int(other)
            mod_int = as_int(mod)
        except ValueError:
            pass
        else:
            return Integer(pow(self.p, other_int, mod_int))

    return super().__pow__(other, mod)

sympy.sympy.core.numbers.__eq__:
def __eq__(self, other):
    try:
        other = _sympify(other)
    except SympifyError:
        return NotImplemented
    if self is other:
        return True
    if other.is_Number and self.is_irrational:
        return False

    return False    # NumberSymbol != non-(Number|self)

sympy.sympy.matrices.expressions.matexpr:
@wraps(func)
def __sympifyit_wrapper(a, b):
    try:
        b = _sympify(b)
        return func(a, b)
    except SympifyError:
        return retval

def args_cnc(self, cset=False, warn=True, **kwargs):
    coeff_c = [x for x in self.args if x.is_commutative]
    coeff_nc = [x for x in self.args if not x.is_commutative]
    if cset:
        clen = len(coeff_c)
        coeff_c = set(coeff_c)
        if clen and warn and len(coeff_c) != clen:
            raise ValueError('repeated commutative arguments: %s' %
                             [ci for ci in coeff_c if list(self.args).count(ci) > 1])
    return [coeff_c, coeff_nc]
