sympy.sympy.core.expr.extract_multiplica:
def extract_multiplicatively(self, c: Expr | complex) -> Expr | None:
    """Return None if it's not possible to make self in the form
       c * something in a nice way, i.e. preserving the properties
       of arguments of self.

       Examples
       ========

       >>> from sympy import symbols, Rational

       >>> x, y = symbols('x,y', real=True)

       >>> ((x*y)**3).extract_multiplicatively(x**2 * y)
       x*y**2

       >>> ((x*y)**3).extract_multiplicatively(x**4 * y)

       >>> (2*x).extract_multiplicatively(2)
       x

       >>> (2*x).extract_multiplicatively(3)

       >>> (Rational(1, 2)*x).extract_multiplicatively(3)
       x/6

    """
    from sympy.functions.elementary.exponential import exp
    from .add import _unevaluated_Add
    c = sympify(c)
    if self is S.NaN:
        return None
    if c is S.One:
        return self
    elif c == self:
        return S.One

    if c.is_Add:
        cc, pc = c.primitive()
        if cc is not S.One:
            c = Mul(cc, pc, evaluate=False)

    if c.is_Mul:
        a, b = c.as_two_terms() # type: ignore
        x = self.extract_multiplicatively(a)
        if x is not None:
            return x.extract_multiplicatively(b)
        else:
            return x

    quotient = self / c
    if self.is_Number:
        if self is S.Infinity:
            if c.is_positive:
                return S.Infinity
        elif self is S.NegativeInfinity:
            if c.is_negative:
                return S.Infinity
            elif c.is_positive:
                return S.NegativeInfinity
        elif self is S.ComplexInfinity:
            if not c.is_zero:
                return S.ComplexInfinity
        elif self.is_Integer:
            if not quotient.is_Integer:
                return None
            elif self.is_positive and quotient.is_negative:
                return None
            else:
                return quotient
        elif self.is_Rational:
            if not quotient.is_Rational:
                return None
            elif self.is_positive and quotient.is_negative:
                return None
            else:
                return quotient
        elif self.is_Float:
            if not quotient.is_Float:
                return None
            elif self.is_positive and quotient.is_negative:
                return None
            else:
                return quotient
    elif self.is_NumberSymbol or self.is_Symbol or self is S.ImaginaryUnit:
        if quotient.is_Mul and len(quotient.args) == 2:
            if quotient.args[0].is_Integer and quotient.args[0].is_positive and quotient.args[1] == self:
                return quotient
        elif quotient.is_Integer and c.is_Number:
            return quotient
    elif self.is_Add:
        cs, ps = self.primitive()
        # assert cs >= 1
        if c.is_Number and c is not S.NegativeOne:
            # assert c != 1 (handled at top)
            if cs is not S.One:
                if c.is_negative:
                    xc = cs.extract_multiplicatively(-c)
                    if xc is not None:
                        xc = -xc
                else:
                    xc = cs.extract_multiplicatively(c)
                if xc is not None:
                    return xc*ps  # rely on 2-arg Mul to restore Add
            return None # |c| != 1 can only be extracted from cs
        if c == ps:
            return cs
        # check args of ps
        newargs = []
        arg: Expr
        for arg in ps.args: # type: ignore
            newarg = arg.extract_multiplicatively(c)
            if newarg is None:
                return None # all or nothing
            newargs.append(newarg)
        if cs is not S.One:
            args = [cs*t for t in newargs]
            # args may be in different order
            return _unevaluated_Add(*args)
        else:
            return Add._from_args(newargs)
    elif self.is_Mul:
        args: list[Expr] = list(self.args) # type: ignore
        for i, arg in enumerate(args):
            newarg = arg.extract_multiplicatively(c)
            if newarg is not None:
                args[i] = newarg
                return Mul(*args)
    elif self.is_Pow or isinstance(self, exp):
        sb, se = self.as_base_exp()
        cb, ce = c.as_base_exp()
        if cb == sb:
            new_exp = se.extract_additively(ce)
            if new_exp is not None:
                return Pow(sb, new_exp)
        elif c == sb:
            new_exp = se.extract_additively(1)
            if new_exp is not None:
                return Pow(sb, new_exp)

    return None

sympy.sympy.core.numbers.__eq__:
def __eq__(self, other):
    try:
        other = _sympify(other)
    except SympifyError:
        return NotImplemented
    if self is other:
        return True
    if other.is_Number and self.is_irrational:
        return False

    return False    # NumberSymbol != non-(Number|self)

sympy.sympy.core.numbers.__mul__:
def __mul__(self, other):
    if global_parameters.evaluate:
        if isinstance(other, int):
            return Integer(self.p*other)
        elif isinstance(other, Integer):
            return Integer(self.p*other.p)
        elif isinstance(other, Rational):
            return Rational._new(self.p*other.p, other.q, igcd(self.p, other.q))
        return Rational.__mul__(self, other)
    return Rational.__mul__(self, other)

sympy.sympy.core.operations._from_args:
@classmethod
def _from_args(cls, args, is_commutative=None):
    """Create new instance with already-processed args.
    If the args are not in canonical order, then a non-canonical
    result will be returned, so use with caution. The order of
    args may change if the sign of the args is changed."""
    if len(args) == 0:
        return cls.identity
    elif len(args) == 1:
        return args[0]

    obj = super().__new__(cls, *args)
    if is_commutative is None:
        is_commutative = fuzzy_and(a.is_commutative for a in args)
    obj.is_commutative = is_commutative
    return obj

sympy.sympy.functions.elementary.exponen:
def as_base_exp(self):
    """
    Returns the 2-tuple (base, exponent).
    """
    return self.func(1), Mul(*self.args)

def __instancecheck__(cls, instance):
    if exp in instance.__class__.__mro__:
        return True
    return isinstance(instance, Pow) and instance.base is S.Exp1

