sympy.sympy.core.decorators._func:
@wraps(func)
def _func(self, other):
    # XXX: The check for _op_priority here should be removed. It is
    # needed to stop mutable matrices from being sympified to
    # immutable matrices which breaks things in quantum...
    if not hasattr(other, '_op_priority'):
        try:
            other = sympify(other, strict=True)
        except SympifyError:
            return retval
    if not isinstance(other, expectedcls):
        return retval
    return func(self, other)

sympy.sympy.core.decorators.__sympifyit_:
@wraps(func)
def __sympifyit_wrapper(a, b):
    try:
        # If an external class has _op_priority, it knows how to deal
        # with SymPy objects. Otherwise, it must be converted.
        if not hasattr(b, '_op_priority'):
            b = sympify(b, strict=True)
        return func(a, b)
    except SympifyError:
        return retval

sympy.sympy.core.mul.as_numer_denom:
def as_numer_denom(self):
    # don't use _from_args to rebuild the numerators and denominators
    # as the order is not guaranteed to be the same once they have
    # been separated from each other
    numers, denoms = list(zip(*[f.as_numer_denom() for f in self.args]))
    return self.func(*numers), self.func(*denoms)

sympy.sympy.core.power.as_numer_denom:
def as_numer_denom(self):
    if not self.is_commutative:
        return self, S.One
    base, exp = self.as_base_exp()
    n, d = base.as_numer_denom()
    # this should be the same as ExpBase.as_numer_denom wrt
    # exponent handling
    neg_exp = exp.is_negative
    if exp.is_Mul and not neg_exp and not exp.is_positive:
        neg_exp = exp.could_extract_minus_sign()
    int_exp = exp.is_integer
    # the denominator cannot be separated from the numerator if
    # its sign is unknown unless the exponent is an integer, e.g.
    # sqrt(a/b) != sqrt(a)/sqrt(b) when a=1 and b=-1. But if the
    # denominator is negative the numerator and denominator can
    # be negated and the denominator (now positive) separated.
    if not (d.is_extended_real or int_exp):
        n = base
        d = S.One
    dnonpos = d.is_nonpositive
    if dnonpos:
        n, d = -n, -d
    elif dnonpos is None and not int_exp:
        n = base
        d = S.One
    if neg_exp:
        n, d = d, n
        exp = -exp
    if exp.is_infinite:
        if n is S.One and d is not S.One:
            return n, self.func(d, exp)
        if n is not S.One and d is S.One:
            return self.func(n, exp), d
    return self.func(n, exp), self.func(d, exp)
