def __lt__(self, other):
    try:
        other = _sympify(other)
    except SympifyError:
        return NotImplemented
    if other.is_Integer:
        return _sympify(self.p < other.p)
    return Rational.__lt__(self, other)

sympy.sympy.core.operations._from_args:
@classmethod
def _from_args(cls, args, is_commutative=None):
    """Create new instance with already-processed args.
    If the args are not in canonical order, then a non-canonical
    result will be returned, so use with caution. The order of
    args may change if the sign of the args is changed."""
    if len(args) == 0:
        return cls.identity
    elif len(args) == 1:
        return args[0]

    obj = super().__new__(cls, *args)
    if is_commutative is None:
        is_commutative = fuzzy_and(a.is_commutative for a in args)
    obj.is_commutative = is_commutative
    return obj

def __new__(cls, *args, evaluate=False, check=None, _sympify=True):
    if not args:
        return cls.identity

    # This must be removed aggressively in the constructor to avoid
    # TypeErrors from GenericZeroMatrix().shape
    args = list(filter(lambda i: cls.identity != i, args))
    if _sympify:
        args = list(map(sympify, args))

    if not all(isinstance(arg, MatrixExpr) for arg in args):
        raise TypeError("Mix of Matrix and Scalar symbols")

    obj = Basic.__new__(cls, *args)

    if check is not None:
        sympy_deprecation_warning(
            "Passing check to MatAdd is deprecated and the check argument will be removed in a future version.",
            deprecated_since_version="1.11",
            active_deprecations_target='remove-check-argument-from-matrix-operations')

    if check is not False:
        validate(*args)

    if evaluate:
        obj = cls._evaluate(obj)

    return obj

sympy.sympy.polys.polytools.gcd:
@public
def gcd(f, g=None, *gens, **args):
    """
    Compute GCD of ``f`` and ``g``.

    Examples
    ========

    >>> from sympy import gcd
    >>> from sympy.abc import x

    >>> gcd(x**2 - 1, x**2 - 3*x + 2)
    x - 1

    """
    if hasattr(f, '__iter__'):
        if g is not None:
            gens = (g,) + gens

        return gcd_list(f, *gens, **args)
    elif g is None:
        raise TypeError("gcd() takes 2 arguments or a sequence of arguments")

    options.allowed_flags(args, ['polys'])

    try:
        (F, G), opt = parallel_poly_from_expr((f, g), *gens, **args)

        # gcd for domain Q[irrational] (purely algebraic irrational)
        a, b = map(sympify, (f, g))
        if a.is_algebraic and a.is_irrational and b.is_algebraic and b.is_irrational:
            frc = (a/b).ratsimp()
            if frc.is_rational:
                # abs ensures that the returned gcd is always non-negative
                return abs(a/frc.as_numer_denom()[0])

    except PolificationFailed as exc:
        domain, (a, b) = construct_domain(exc.exprs)

        try:
            return domain.to_sympy(domain.gcd(a, b))
        except NotImplementedError:
            raise ComputationFailed('gcd', 2, exc)

    result = F.gcd(G)

    if not opt.polys:
        return result.as_expr()
    else:
        return result

sympy.sympy.utilities.iterables.common_s:
def common_suffix(*seqs):
    """Return the subsequence that is a common ending of sequences in ``seqs``.

    >>> from sympy.utilities.iterables import common_suffix
    >>> common_suffix(list(range(3)))
    [0, 1, 2]
    >>> common_suffix(list(range(3)), list(range(4)))
    []
    >>> common_suffix([1, 2, 3], [9, 2, 3])
    [2, 3]
    >>> common_suffix([1, 2, 3], [9, 7, 3])
    [3]
    """

    if not all(seqs):
        return []
    elif len(seqs) == 1:
        return seqs[0]
    i = 0
    for i in range(-1, -min(len(s) for s in seqs) - 1, -1):
        if not all(seqs[j][i] == seqs[0][i] for j in range(len(seqs))):
            break
    else:
        i -= 1
    if i == -1:
        return []
    else:
        return seqs[0][i + 1:]
