sympy.sympy.core.traversal._preorder_tra:
def _preorder_traversal(self, node, keys):
    yield node
    if self._skip_flag:
        self._skip_flag = False
        return
    if isinstance(node, Basic):
        if not keys and hasattr(node, '_argset'):
            # LatticeOp keeps args as a set. We should use this if we
            # don't care about the order, to prevent unnecessary sorting.
            args = node._argset
        else:
            args = node.args
        if keys:
            if keys != True:
                args = ordered(args, keys, default=False)
            else:
                args = ordered(args)
        for arg in args:
            yield from self._preorder_traversal(arg, keys)
    elif iterable(node):
        for item in node:
            yield from self._preorder_traversal(item, keys)

sympy.sympy.matrices.common.__init__:
def __init__(self, rows, cols=None, mat=None, copy=False):
    if isfunction(mat):
        # if we passed in a function, use that to populate the indices
        mat = [mat(i, j) for i in range(rows) for j in range(cols)]
    if cols is None and mat is None:
        mat = rows
    rows, cols = getattr(mat, 'shape', (rows, cols))
    try:
        # if we passed in a list of lists, flatten it and set the size
        if cols is None and mat is None:
            mat = rows
        cols = len(mat[0])
        rows = len(mat)
        mat = [x for l in mat for x in l]
    except (IndexError, TypeError):
        pass
    self.mat = tuple(self._sympify(x) for x in mat)
    self.rows, self.cols = rows, cols
    if self.rows is None or self.cols is None:
        raise NotImplementedError("Cannot initialize matrix with given parameters")

sympy.sympy.matrices.immutable.sympify_m:
def sympify_mpmath_matrix(arg):
    mat = [_sympify(x) for x in arg]
    return ImmutableDenseMatrix(arg.rows, arg.cols, mat)
