def __new__(cls, *args, **kwargs):
    if kwargs.get('sympify', True):
        args = (sympify(arg) for arg in args)
    obj = Basic.__new__(cls, *args)
    return obj

sympy.sympy.core.operations.__new__:
def __new__(cls, *args, evaluate=None, **options):
    args = (_sympify_(arg) for arg in args)

    if evaluate is None:
        evaluate = global_parameters.evaluate

    if not evaluate:
        obj = super().__new__(cls, *args, evaluate=False, **options)
        obj._argset = frozenset(args)
        return obj

    try:
        # /!\ args is a generator and _new_args_filter
        # must be careful to handle as such; this
        # is done so short-circuiting can be done
        # without having to sympify all values
        _args = frozenset(cls._new_args_filter(args))
    except ShortCircuit:
        return sympify(cls.zero)
    if not _args:
        return sympify(cls.identity)
    elif len(_args) == 1:
        return set(_args).pop()
    else:
        # XXX in almost every other case for __new__, *_args is
        # passed along, but the expectation here is for _args
        obj = super(AssocOp, cls).__new__(cls, *ordered(_args))
        obj._argset = _args
        return obj

sympy.sympy.core.relational.__new__:
def __new__(cls, lhs: Expr | complex, rhs: Expr | complex, **options) -> Self | BooleanTrue | BooleanFalse: # type: ignore

    try:
        lhs_e = _sympify(lhs)
        rhs_e = _sympify(rhs)
    except SympifyError:
        return NotImplemented

    evaluate = options.pop('evaluate', global_parameters.evaluate)
    if evaluate:
        for me in (lhs_e, rhs_e):
            if me.is_extended_real is False:
                raise TypeError("Invalid comparison of non-real %s" % me)
            if me is S.NaN:
                raise TypeError("Invalid NaN comparison")
        # First we invoke the appropriate inequality method of `lhs`
        # (e.g., `lhs.__lt__`).  That method will try to reduce to
        # boolean or raise an exception.  It may keep calling
        # superclasses until it reaches `Expr` (e.g., `Expr.__lt__`).
        # In some cases, `Expr` will just invoke us again (if neither it
        # nor a subclass was able to reduce to boolean or raise an
        # exception).  In that case, it must call us with
        # `evaluate=False` to prevent infinite recursion.
        return cls._eval_relation(lhs_e, rhs_e, **options)

    # make a "non-evaluated" Expr for the inequality
    return Relational.__new__(cls, lhs_e, rhs_e, **options)

sympy.sympy.functions.elementary.piecewi:
def __new__(cls, *args, **options):
    if len(args) == 0:
        raise TypeError("At least one (expr, cond) pair expected.")
    # (Try to) sympify args first
    newargs = []
    for ec in args:
        # ec could be a ExprCondPair or a tuple
        pair = ExprCondPair(*getattr(ec, 'args', ec))
        cond = pair.cond
        if cond is false:
            continue
        newargs.append(pair)
        if cond is true:
            break

    eval = options.pop('evaluate', global_parameters.evaluate)
    if eval:
        r = cls.eval(*newargs)
        if r is not None:
            return r
    elif len(newargs) == 1 and newargs[0].cond == True:
        return newargs[0].expr

    return Basic.__new__(cls, *newargs, **options)

