sympy.sympy.polys.polyoptions.getter:
@property
def getter(self):
    try:
        return self[cls.option]
    except KeyError:
        return cls.default()

sympy.sympy.polys.polyoptions.build_opti:
def build_options(gens, args=None):
    """Construct options from keyword arguments or ... options. """
    if args is None:
        gens, args = (), gens

    if len(args) != 1 or 'opt' not in args or gens:
        return Options(gens, args)
    else:
        return args['opt']

sympy.sympy.polys.polyutils.order_key:
def order_key(gen):
    gen = str(gen)

    if wrt is not None:
        try:
            return (-len(wrt) + wrt.index(gen), gen, 0)
        except ValueError:
            pass

    name, index = _re_gen.match(gen).groups()

    if index:
        index = int(index)
    else:
        index = 0

    try:
        return ( gens_order[name], name, index)
    except KeyError:
        pass

    try:
        return (_gens_order[name], name, index)
    except KeyError:
        pass

    return (_max_order, name, index)
