sympy.sympy.core.add.__neg__:
def __neg__(self):
    if not global_parameters.distribute:
        return super().__neg__()
    return Mul(S.NegativeOne, self)

sympy.sympy.core.assumptions.getit:
def getit(self):
    try:
        return self._assumptions[fact]
    except KeyError:
        if self._assumptions is self.default_assumptions:
            self._assumptions = self.default_assumptions.copy()
        return _ask(fact, self)

sympy.sympy.core.cache.wrapper:
@wraps(func)
def wrapper(*args, **kwargs):
    try:
        retval = cfunc(*args, **kwargs)
    except TypeError as e:
        if not e.args or not e.args[0].startswith('unhashable type:'):
            raise
        retval = func(*args, **kwargs)
    return retval

sympy.sympy.core.expr.__neg__:
def __neg__(self) -> Expr:
    # Mul has its own __neg__ routine, so we just
    # create a 2-args Mul with the -1 in the canonical
    # slot 0.
    c = self.is_commutative
    return Mul._from_args((S.NegativeOne, self), c)

sympy.sympy.core.mul.__neg__:
def __neg__(self):
    c, args = self.as_coeff_mul()
    if args[0] is not S.ComplexInfinity:
        c = -c
    if c is not S.One:
        if args[0].is_Number:
            args = list(args)
            if c is S.NegativeOne:
                args[0] = -args[0]
            else:
                args[0] *= c
        else:
            args = (c,) + args
    return self._from_args(args, self.is_commutative)

sympy.sympy.core.power.as_numer_denom:
def as_numer_denom(self):
    if not self.is_commutative:
        return self, S.One
    base, exp = self.as_base_exp()
    n, d = base.as_numer_denom()
    # this should be the same as ExpBase.as_numer_denom wrt
    # exponent handling
    neg_exp = exp.is_negative
    if exp.is_Mul and not neg_exp and not exp.is_positive:
        neg_exp = exp.could_extract_minus_sign()
    int_exp = exp.is_integer
    # the denominator cannot be separated from the numerator if
    # its sign is unknown unless the exponent is an integer, e.g.
    # sqrt(a/b) != sqrt(a)/sqrt(b) when a=1 and b=-1. But if the
    # denominator is negative the numerator and denominator can
    # be negated and the denominator (now positive) separated.
    if not (d.is_extended_real or int_exp):
        n = base
        d = S.One
    dnonpos = d.is_nonpositive
    if dnonpos:
        n, d = -n, -d
    elif dnonpos is None and not int_exp:
        n = base
        d = S.One
    if neg_exp:
        n, d = d, n
        exp = -exp
    if exp.is_infinite:
        if n is S.One and d is not S.One:
            return n, self.func(d, exp)
        if n is not S.One and d is S.One:
            return self.func(n, exp), d
    return self.func(n, exp), self.func(d, exp)

sympy.sympy.core.symbol.is_constant:
def is_constant(self, *wrt, **flags):
    if not wrt:
        return False
    return self not in wrt

sympy.sympy.functions.elementary.exponen:
def __instancecheck__(cls, instance):
    if exp in instance.__class__.__mro__:
        return True
    return isinstance(instance, Pow) and instance.base is S.Exp1

def as_base_exp(self):
    """
    Returns the 2-tuple (base, exponent).
    """
    return self.func(1), Mul(*self.args)
